import { alpha, createTheme, CssBaseline, ThemeProvider as MUIThemeProvider } from '@mui/material';

const PRIMARY = {
  lighter: "#E3F2FD",
  light: "#90CAF9",
  main: "#2196f3", // Giữ nguyên cho nhất quán
  dark: "#1976d2",
  darker: "#0d47a1",
  contrastText: "#FFF",
};
const SECONDARY = {
  lighter: "#f3e5f5",
  light: "#e1bee7",
  main: "#9c27b0", // Giữ nguyên cho nhất quán
  dark: "#7b1fa2",
  darker: "#4a0072",
  contrastText: "#FFF",
};
const SUCCESS = {
  lighter: "#e8f5e9",
  light: "#a5d6a7",
  main: "#4caf50", // Giữ nguyên cho nhất quán
  dark: "#388e3c",
  darker: "#1b5e20",
  contrastText: "#FFF",
};

const GREY = {
  0: "#FFFFFF",
  100: "#F9FAFB",
  200: "#F4F6F8",
  300: "#DFE3E8",
  400: "#C4CDD5",
  500: "#919EAB",
  600: "#637381",
  700: "#454F5B",
  800: "#212B36",
  900: "#161C24",
  500_8: alpha("#919EAB", 0.08),
  500_12: alpha("#919EAB", 0.12),
  500_16: alpha("#919EAB", 0.16),
  500_24: alpha("#919EAB", 0.24),
  500_32: alpha("#919EAB", 0.32),
  500_48: alpha("#919EAB", 0.48),
  500_56: alpha("#919EAB", 0.56),
  500_80: alpha("#919EAB", 0.8),
};

function ThemeProvider({ children }) {
  const themeOptions = {
    palette: {
      mode: 'dark', // Thêm chế độ tối
      primary: PRIMARY,
      secondary: SECONDARY,
      success: SUCCESS,
      text: {
        primary: GREY[0], // Văn bản chính sáng
        secondary: GREY[300], // Văn bản phụ sáng hơn
        disabled: GREY[600], // Văn bản bị vô hiệu hóa
      },
      background: {
        paper: GREY[800], // Nền thẻ
        default: GREY[900], // Nền tổng quát
        neutral: GREY[700], // Nền trung tính
      },
      action: {
        active: GREY[500], // Màu khi hành động
        hover: GREY[500_8], // Màu khi hover
        selected: GREY[500_16], // Màu khi được chọn
        disabled: GREY[500_80], // Màu khi bị vô hiệu hóa
        disabledBackground: GREY[500_24], // Nền khi bị vô hiệu hóa
        focus: GREY[500_24], // Màu khi tập trung
        hoverOpacity: 0.08,
        disabledOpacity: 0.48,
      },
      divider: GREY[700], // Màu phân chia
    },
    shape: { borderRadius: 8 },
  };

  const theme = createTheme(themeOptions);

  return (
    <MUIThemeProvider theme={theme}>
      <CssBaseline />
      {children}
    </MUIThemeProvider>
  );
}

export default ThemeProvider;
